import java.util.Map;

@Service
public class UserServiceImpl implements UserService {

    @Autowired
    private UserRepository userRepository;

    @Override
    public UserEntity getUserByEmail(String email) {
        return userRepository.findByEmail(email);
    }

    @Override
    public Map<String, Object> getUserProfile(String email) {
        UserEntity user = userRepository.findByEmail(email);
        if (user == null) {
            return null;
        }

        Map<String, Object> profile = new HashMap<>();
        profile.put("id", user.getId());
        profile.put("name", user.getName());
        profile.put("email", user.getEmail());
        profile.put("role", user.getRole());

        // Include the about field if it exists
        if (user.getAbout() != null) {
            profile.put("about", user.getAbout());
        }

        // Include resume information if it exists
        if (user.getResume() != null && user.getResume().length > 0) {
            profile.put("hasResume", true);
            profile.put("resumeFileName", user.getResumeFileName());
        } else {
            profile.put("hasResume", false);
        }

        return profile;
    }

    @Override
    public void updateUserProfile(String email, Map<String, String> profileData) {
        UserEntity user = userRepository.findByEmail(email);
        if (user == null) {
            throw new RuntimeException("User not found");
        }

        // Update the about field if provided
        if (profileData.containsKey("about")) {
            user.setAbout(profileData.get("about"));
        }

        // Update name if provided
        if (profileData.containsKey("name")) {
            user.setName(profileData.get("name"));
        }

        userRepository.save(user);
    }

    @Override
    public void uploadResume(String email, MultipartFile file) throws IOException {
        UserEntity user = userRepository.findByEmail(email);
        if (user == null) {
            throw new RuntimeException("User not found");
        }

        user.setResume(file.getBytes());
        user.setResumeFileName(file.getOriginalFilename());
        userRepository.save(user);
    }

    @Override
    public byte[] getResume(String email) {
        UserEntity user = userRepository.findByEmail(email);
        if (user == null || user.getResume() == null) {
            return null;
        }
        return user.getResume();
    }

    @Override
    public String getResumeFileName(String email) {
        UserEntity user = userRepository.findByEmail(email);
        if (user == null) {
            return null;
        }
        return user.getResumeFileName();
    }
}
