    private UserRepository userRepository;

    @Override
    public void applyForProject(String studentEmail, Long projectId) {
        UserEntity student = userRepository.findByEmail(studentEmail);
        if (student == null) {
            throw new RuntimeException("User not found");
        }

        // Check if user is a student
        if (student.getRole() != USER_ROLES.ROLE_STUDENT) {
            throw new RuntimeException("Only students can apply for projects");
        }

        // Find the project
        Optional<Project> projectOpt = projectRepository.findById(projectId);
        if (!projectOpt.isPresent()) {
            throw new RuntimeException("Project not found");
        }

        Project project = projectOpt.get();

        // Check if already applied
        Application existingApplication = applicationRepository.findByStudentAndProject(student, project);
        if (existingApplication != null) {
            throw new RuntimeException("You have already applied for this project");
        }

        // Create new application
        Application application = new Application();
        application.setStudent(student);
        application.setProject(project);
        application.setStatus("APPLIED");
        application.setAppliedAt(LocalDate.now());

        applicationRepository.save(application);
    }

    @Override
    public List<Application> getStudentApplications(String studentEmail) {
        UserEntity student = userRepository.findByEmail(studentEmail);
        if (student == null) {
            throw new RuntimeException("User not found");
        }

        return applicationRepository.findByStudent(student);
    }

    @Override
    public List<Application> getProjectApplications(String facultyEmail, Long projectId) {
        UserEntity faculty = userRepository.findByEmail(facultyEmail);
        if (faculty == null) {
            throw new RuntimeException("User not found");
        }

        // Check if user is a faculty
        if (faculty.getRole() != USER_ROLES.ROLE_FACULTY) {
            throw new RuntimeException("Only faculty can view project applications");
        }

        Optional<Project> projectOpt = projectRepository.findById(projectId);
        if (!projectOpt.isPresent()) {
            throw new RuntimeException("Project not found");
        }

        Project project = projectOpt.get();

        // Ensure the faculty owns this project
        if (!project.getFaculty().getId().equals(faculty.getId())) {
            throw new RuntimeException("You don't have permission to view these applications");
        }

        return applicationRepository.findByProject(project);
    }

    @Override
    public void updateApplicationStatus(String facultyEmail, Long applicationId, String newStatus) {
        UserEntity faculty = userRepository.findByEmail(facultyEmail);
        if (faculty == null) {
            throw new RuntimeException("User not found");
        }

        // Check if user is a faculty
        if (faculty.getRole() != USER_ROLES.ROLE_FACULTY) {
            throw new RuntimeException("Only faculty can update application status");
        }

        Optional<Application> applicationOpt = applicationRepository.findById(applicationId);
        if (!applicationOpt.isPresent()) {
            throw new RuntimeException("Application not found");
        }

        Application application = applicationOpt.get();

        // Ensure the faculty owns the project this application is for
        if (!application.getProject().getFaculty().getId().equals(faculty.getId())) {
            throw new RuntimeException("You don't have permission to update this application");
        }

        if (newStatus == null || (!newStatus.equals("ACCEPTED") && !newStatus.equals("REJECTED"))) {
            throw new RuntimeException("Status must be either ACCEPTED or REJECTED");
        }

        application.setStatus(newStatus);
        applicationRepository.save(application);
    }
}
