@RequestMapping("/api/applications")
public class ApplicationController {

    @Autowired
    private ApplicationService applicationService;

    @Autowired
    private JwtProvider jwtProvider;

    @PostMapping("/apply/{projectId}")
    public ResponseEntity<?> applyForProject(
            @RequestHeader("Authorization") String token,
            @PathVariable Long projectId) {

        // Validate the token
        if (token == null || !token.startsWith("Bearer ")) {
            return ResponseEntity.status(HttpStatus.UNAUTHORIZED).body("Invalid or missing token");
        }

        if (!jwtProvider.validateToken(token)) {
            return ResponseEntity.status(HttpStatus.UNAUTHORIZED).body("Invalid token");
        }

        try {
            // Extract email from token
            String email = jwtProvider.getEmailfromToken(token);
            applicationService.applyForProject(email, projectId);
            return ResponseEntity.status(HttpStatus.CREATED).body("Application submitted successfully");
        } catch (Exception e) {
            if (e.getMessage().contains("Only students can apply")) {
                return ResponseEntity.status(HttpStatus.FORBIDDEN).body(e.getMessage());
            } else if (e.getMessage().contains("already applied")) {
                return ResponseEntity.status(HttpStatus.CONFLICT).body(e.getMessage());
            } else if (e.getMessage().contains("not found")) {
                return ResponseEntity.status(HttpStatus.NOT_FOUND).body(e.getMessage());
            } else {
                return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR).body(e.getMessage());
            }
        }
    }

